// Extract bills from response and filter out 'introduced' status
const items = $input.all();
const response = items[0].json;
const bills = response.items || [];

// Only process bills that are actively moving (not just 'introduced')
const activeBills = bills.filter(bill => {
  const status = bill.status;
  // Exclude bills that are only 'introduced' - we want bills in committee or beyond
  return status && status !== 'introduced';
});

console.log(`Filtered ${bills.length} bills down to ${activeBills.length} active bills (excluded 'introduced' status)`);

// Return each active bill as a separate item for processing
return activeBills.map(bill => ({ json: bill }));

// Determine if bill is popular based on search results
// The search result and bill data are both available in different ways
// We'll extract bill data from the search query that was made

const items = $input.all();
const results = [];

for (let i = 0; i < items.length; i++) {
  const searchResult = items[i].json;

  // Get bill data from the input item's binary/pairedItem reference
  // In n8n, when Split in Batches passes data through Search Web for Bill,
  // the original bill data is lost. We need to extract it from the query string.

  // The search query was: "HR 3616 119th Congress"
  // We can get it from the request URL or reconstruct from search results
  const searchQuery = searchResult?.queries?.request?.[0]?.searchTerms || '';
  const queryParts = searchQuery.match(/^([A-Z]+)\s+(\d+)\s+(\d+)th/);

  if (!queryParts) {
    console.log('Could not parse query:', searchQuery);
    continue;
  }

  const billType = queryParts[1].toLowerCase();
  const billNumber = parseInt(queryParts[2], 10);
  const congress = parseInt(queryParts[3], 10);

  // Calculate popularity score based on quality signals, not just raw count
  let popularityScore = 0;
  let isPopular = false;

  if (searchResult && !searchResult.error && searchResult.items) {
    const items = searchResult.items || [];

    // Score each result based on recency and source quality
    for (const item of items) {
      let score = 1; // Base score per result

      // Boost for high-quality domains (news sources, government, etc.)
      const domain = item.displayLink || '';
      if (domain.includes('news') || domain.includes('cnn') || domain.includes('fox') || 
          domain.includes('nbc') || domain.includes('abc') || domain.includes('reuters') ||
          domain.includes('ap.org') || domain.includes('politico') || domain.includes('thehill')) {
        score += 5; // Major news = +5
      } else if (domain.includes('.gov') || domain.includes('congress.gov')) {
        score += 3; // Government source = +3
      } else if (domain.includes('blog') || domain.includes('wordpress') || domain.includes('medium')) {
        score -= 0.5; // Blogs/opinion = -0.5
      }

      // Boost if bill number is in the title (more relevant)
      const title = (item.title || '').toLowerCase();
      if (title.includes(billType) && title.includes(billNumber.toString())) {
        score += 2;
      }

      popularityScore += score;
    }

    // Round to nearest integer
    popularityScore = Math.round(popularityScore);

    // Mark as popular if score >= 10 (roughly 2-3 quality news mentions)
    isPopular = popularityScore >= 10;
  }

  // Get title from search results
  const title = searchResult?.items?.[0]?.title || `${billType.toUpperCase()} ${billNumber}`;

  results.push({
    json: {
      bill_type: billType,
      bill_number: billNumber,
      congress: congress,
      title: title,
      popularity_score: popularityScore,
      is_popular: isPopular
    }
  });
}

return results;
