// Generate list of all presidents to fetch
const PRESIDENT_CONGRESS_MAP = {
  "Donald Trump 2nd": { start: 119, end: 119 },
  "Joe Biden": { start: 117, end: 118 },
  "Donald Trump": { start: 115, end: 116 },
  "Barack Obama": { start: 111, end: 114 },
  "George W. Bush": { start: 107, end: 110 },
  "Bill Clinton": { start: 103, end: 106 },
  "George H.W. Bush": { start: 101, end: 102 },
};

const presidents = Object.entries(PRESIDENT_CONGRESS_MAP).map(([name, range]) => ({
  json: {
    president_name: name,
    start_congress: range.start,
    end_congress: range.end
  }
}));

return presidents;

// Generate array of congress numbers from start to end
const start = $input.first().json.start_congress;
const end = $input.first().json.end_congress;
const presName = $input.first().json.president_name;

const congresses = [];
// Iterate from higher congress to lower (or equal)
const low = Math.min(start, end);
const high = Math.max(start, end);
for (let c = high; c >= low; c--) {
  congresses.push({ congress: c, president_name: presName });
}

// Return in n8n format with json wrapper
return congresses.map(c => ({ json: c }));

// Filter for enacted bills only and extract relevant data
const bills = $input.first().json.bills || [];

const enactedBills = bills.filter(bill => {
  const latestAction = (bill.latestAction?.text || '').toLowerCase();
  return latestAction.includes('became public law') || 
         latestAction.includes('became law') ||
         latestAction.includes('signed by president');
});

// Map to our ingestion format with proper n8n wrapper
return enactedBills.map(bill => ({
  json: {
    congress: bill.congress || 119,
    bill_type: (bill.type || 'hr').toLowerCase(),
    bill_number: parseInt(bill.number || 0)
  }
}));

// Summarize results
const items = $input.all();

let success = 0;
let failed = 0;
let skipped = 0;

for (const item of items) {
  if (item.json.error) {
    failed++;
  } else if (item.json.status === 'unchanged' || item.json.status === 'partial') {
    skipped++;
  } else {
    success++;
  }
}

return [{
  json: {
    total_processed: items.length,
    success: success,
    failed: failed,
    skipped: skipped,
    timestamp: new Date().toISOString()
  }
}];

// Get president name and filter bills
const presidentName = $('Generate Congress List').first().json.president_name;
const allBills = $input.first().json.items || [];

// President date ranges
const PRESIDENT_RANGES = {
  "Donald Trump 2nd": { start: "2025-01-20", end: "2029-01-20" },
  "Joe Biden": { start: "2021-01-20", end: "2025-01-20" },
  "Donald Trump": { start: "2017-01-20", end: "2021-01-20" },
  "Barack Obama": { start: "2009-01-20", end: "2017-01-20" },
  "George W. Bush": { start: "2001-01-20", end: "2009-01-20" },
  "Bill Clinton": { start: "1993-01-20", end: "2001-01-20" },
  "George H.W. Bush": { start: "1989-01-20", end: "1993-01-20" },
};

const range = PRESIDENT_RANGES[presidentName];
if (!range) {
  return [];
}

const startDate = new Date(range.start);
const endDate = new Date(range.end);

// Filter bills by date range
const filteredBills = allBills.filter(bill => {
  if (!bill.latest_action_date) return false;
  const actionDate = new Date(bill.latest_action_date);
  return actionDate >= startDate && actionDate < endDate;
});

// Limit to top 10 to avoid too many API calls
return filteredBills.slice(0, 10).map(bill => ({
  json: {
    bill_id: bill.id,
    bill_type: bill.bill_type,
    bill_number: bill.bill_number,
    congress: bill.congress,
    title: bill.title,
    president_name: presidentName
  }
}));

// Determine if bill is popular based on search results
// The search result and bill data are both available in different ways
// We'll extract bill data from the search query that was made

const items = $input.all();
const results = [];

for (let i = 0; i < items.length; i++) {
  const searchResult = items[i].json;

  // Get bill data from the input item's binary/pairedItem reference
  // In n8n, when Split in Batches passes data through Search Web for Bill,
  // the original bill data is lost. We need to extract it from the query string.

  // The search query was: "HR 3616 119th Congress"
  // We can get it from the request URL or reconstruct from search results
  const searchQuery = searchResult?.queries?.request?.[0]?.searchTerms || '';
  const queryParts = searchQuery.match(/^([A-Z]+)\s+(\d+)\s+(\d+)th/);

  if (!queryParts) {
    console.log('Could not parse query:', searchQuery);
    continue;
  }

  const billType = queryParts[1].toLowerCase();
  const billNumber = parseInt(queryParts[2], 10);
  const congress = parseInt(queryParts[3], 10);

  // Calculate popularity score based on quality signals, not just raw count
  let popularityScore = 0;
  let isPopular = false;

  if (searchResult && !searchResult.error && searchResult.items) {
    const items = searchResult.items || [];

    // Score each result based on recency and source quality
    for (const item of items) {
      let score = 1; // Base score per result

      // Boost for high-quality domains (news sources, government, etc.)
      const domain = item.displayLink || '';
      if (domain.includes('news') || domain.includes('cnn') || domain.includes('fox') || 
          domain.includes('nbc') || domain.includes('abc') || domain.includes('reuters') ||
          domain.includes('ap.org') || domain.includes('politico') || domain.includes('thehill')) {
        score += 5; // Major news = +5
      } else if (domain.includes('.gov') || domain.includes('congress.gov')) {
        score += 3; // Government source = +3
      } else if (domain.includes('blog') || domain.includes('wordpress') || domain.includes('medium')) {
        score -= 0.5; // Blogs/opinion = -0.5
      }

      // Boost if bill number is in the title (more relevant)
      const title = (item.title || '').toLowerCase();
      if (title.includes(billType) && title.includes(billNumber.toString())) {
        score += 2;
      }

      popularityScore += score;
    }

    // Round to nearest integer
    popularityScore = Math.round(popularityScore);

    // Mark as popular if score >= 10 (roughly 2-3 quality news mentions)
    isPopular = popularityScore >= 10;
  }

  // Get title from search results
  const title = searchResult?.items?.[0]?.title || `${billType.toUpperCase()} ${billNumber}`;

  results.push({
    json: {
      bill_type: billType,
      bill_number: billNumber,
      congress: congress,
      title: title,
      popularity_score: popularityScore,
      is_popular: isPopular
    }
  });
}

return results;
