// Generate list of all presidents with their congress sessions
// CORRECTED congress mappings:
// - Trump 1st term: 115-116 (2017-2021)
// - Biden: 117-118 (2021-2025)
// - Trump 2nd term: 119 (2025-2029)
// - Obama: 111-114 (2009-2017)
// - Bush: 107-110 (2001-2009)
// - Clinton: 103-106 (1993-2001)
// - H.W. Bush: 101-102 (1989-1993)
const presidents = [
  { name: "Donald Trump 2nd", congresses: [119] },
  { name: "Joe Biden", congresses: [117, 118] },
  { name: "Donald Trump", congresses: [115, 116] },
  { name: "Barack Obama", congresses: [111, 112, 113, 114] },
  { name: "George W. Bush", congresses: [107, 108, 109, 110] },
  { name: "Bill Clinton", congresses: [103, 104, 105, 106] },
  { name: "George H.W. Bush", congresses: [101, 102] }
];

// Flatten to one item per congress session
const items = [];
for (const pres of presidents) {
  for (const congress of pres.congresses) {
    items.push({
      json: {
        president: pres.name,
        congress: congress
      }
    });
  }
}

return items;

// Process ALL items (all congress sessions) from the /law endpoint
// The /law endpoint returns 'bills' array with enacted laws
const allItems = $input.all();
const allEnacted = [];

for (const item of allItems) {
  // The /law endpoint returns 'bills' array (bills that became law)
  const bills = item.json.bills || [];

  for (const bill of bills) {
    // Each bill has type (S, HR, etc.) and number
    if (bill.type && bill.number) {
      allEnacted.push({
        json: {
          congress: bill.congress,
          bill_type: bill.type.toLowerCase(),
          bill_number: parseInt(bill.number, 10)
        }
      });
    }
  }
}

// If no enacted bills found, return empty to skip
if (allEnacted.length === 0) {
  return [{ json: { _empty: true } }];
}

return allEnacted;
